mapper.insert:


	SELECT NVL(MAX(bno),1000) + 1
	FROM tbl_board_01


INSERT INTO tbl_board_01 (
	 bno
     , title
     , contents
     , writer
     , image
)
VALUES
(
	#{bno}
    , #{title}
    , #{contents}
    , #{writer}
    , #{image}
)

mapper.update:
UPDATE tbl_board_01
	SET
		title = #{title}
     	,contents = #{contents}
    	,writer = #{writer}
    	,updatedate = sysdate
     	,image = #{image}
    WHERE bno = #{bno}
